import React, { useState, useEffect } from "react";
import "./App.css";
import * as THREE from "three";
import { ARButton } from "three/examples/jsm/webxr/ARButton";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";

function App() {
  const [arActive, setArActive] = useState(false);

  useEffect(() => {
    let scene, camera, renderer;
    let models = [
      "./Rheina-Chair.glb",
      "./Round-Table.glb",
      "./Renata-Square-Dining-Table.glb",
      "./Lala-Chair.glb",
      "./Almira-Bar-Table.glb",
      "./Renata-Big-Recta-Dining-Table.glb",
      "./Rama-Big-Round-Table.glb",
      "./Alma-Chair.glb",
      "./Deva-Round-SIde-Table.glb",
      "./High-Baack-Stool.glb",
      "./Indian-Barstool.glb",
      "./Lily-Side-Chair.glb",
      "./Jungle-Side-Table.glb",
      "./Rama-Small-Round-Table.glb",
      "./Renata-Side-Table.glb",
      "./Altha-Chair.glb",
      "./Lulu-Chair.glb",
    ];
    let items = [];
    let controller;
    let reticle;

    init();
    setupFurnitureSelection();
    animate();

    function init() {
      let myCanvas = document.getElementById("canvas");
      scene = new THREE.Scene();
      camera = new THREE.PerspectiveCamera(70, myCanvas.innerWidth / myCanvas.innerHeight, 0.01, 20);

      const light = new THREE.HemisphereLight(0xffffff, 0xbbbbff, 1);
      light.position.set(0.5, 1, 0.25);
      scene.add(light);

      renderer = new THREE.WebGLRenderer({
        canvas: myCanvas,
        antialias: true,
        alpha: true,
      });
      renderer.setPixelRatio(window.devicePixelRatio);
      renderer.setSize(myCanvas.innerWidth, myCanvas.innerHeight);
      renderer.xr.enabled = true;

      const arButton = ARButton.createButton(renderer, {
        requiredFeatures: ["hit-test"],
        optionalFeatures: ["dom-overlay", "light-estimation"],
        domOverlay: { root: document.body },
      });
      arButton.style.bottom = "22%";
      document.body.appendChild(arButton);

      arButton.addEventListener('click', () => {
        setArActive(true);
      });

      for (let i = 0; i < models.length; i++) {
        const loader = new GLTFLoader();
        loader.load(models[i], function (glb) {
          let model = glb.scene;
          items[i] = model;
        });
      }

      controller = renderer.xr.getController(0);
      controller.addEventListener("select", onSelect);
      scene.add(controller);

      reticle = new THREE.Mesh(new THREE.RingGeometry(0.15, 0.2, 32).rotateX(-Math.PI / 2), new THREE.MeshBasicMaterial());
      reticle.matrixAutoUpdate = false;
      reticle.visible = false;
      scene.add(reticle);
    }

    function onSelect() {
      if (reticle.visible) {
        let newModel = items[0].clone(); // Change index as needed
        newModel.visible = true;

        reticle.matrix.decompose(newModel.position, newModel.quaternion, newModel.scale);

        scene.add(newModel);
      }
    }

    function setupFurnitureSelection() {
      for (let i = 0; i < models.length; i++) {
        const el = document.querySelector(`#item` + i);
        el.addEventListener("beforexrselect", (e) => {
          e.preventDefault();
          e.stopPropagation();
        });
        el.addEventListener("click", (e) => {
          e.preventDefault();
          e.stopPropagation();
          onClicked(e, items[i], i);
        });
      }
    }

    function onClicked(e, selectItem, index) {
      // Handle item selection
    }

    function animate() {
      renderer.setAnimationLoop(render);
    }

    function render(timestamp, frame) {
      renderer.render(scene, camera);
    }

  }, [arActive]); // Dependency array includes arActive to re-run effect when it changes

  return (
    <div className="App">
      <canvas id="canvas"></canvas>
      <div className={`scanning-message ${arActive ? 'visible' : ''}`}>
        <img src="your-image-url-here" alt="Scanning" className="scanning-image" />
        <div className="typing-text">Our System Scanning your Surface</div>
      </div>
    </div>
  );
}

export default App;
